    private void btnSaveActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnSaveActionPerformed
// TODO 將在此處加入您的處理代碼：
        String sqlstr = "Insert into score values(2,'GM',1,77,88,99)";
        int rec =0;
        
        try {
            rec = rst.getRow();
            smt.executeUpdate(sqlstr);        
            rst = smt.executeQuery("select * from score");                                
            rst.last();
            RecordCount = rst.getRow();
            rst.absolute(rec);
            showData();
        } catch (SQLException ex) {
            ex.printStackTrace();
        }        
                
    }//GEN-LAST:event_btnSaveActionPerformed

