    private void btnNextActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnNextActionPerformed
        try {
// TODO 將在此處加入您的處理代碼：
            if(!rst.isLast()){
                rst.next();
                showData();
            }else{
                javax.swing.JOptionPane.showMessageDialog(null,"記錄已在最末筆!");
            }
        } catch (SQLException ex) {
            ex.printStackTrace();
        }
    }//GEN-LAST:event_btnNextActionPerformed
    private void userInitComponents(){
        try {
            
            Class.forName("sun.jdbc.odbc.JdbcOdbcDriver");
            cn = DriverManager.getConnection("jdbc:odbc:MyAccess");
            smt = cn.createStatement(ResultSet.TYPE_SCROLL_INSENSITIVE,ResultSet.CONCUR_READ_ONLY);
            rst =  smt.executeQuery("Select * from SCORE");
            rst.last();
            RecordCount = rst.getRow();
            rst.first();
            showData();
        } catch (ClassNotFoundException ex) {
            ex.printStackTrace();
        } catch (SQLException ex) {
            ex.printStackTrace();
        }
        
    }
    private void btnPrevActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnPrevActionPerformed
        try {
// TODO 將在此處加入您的處理代碼：
            if(!rst.isFirst()){
                rst.previous();
                showData();
            }else{
                javax.swing.JOptionPane.showMessageDialog(null,"記錄已在第一筆!");
            }
        } catch (SQLException ex) {
            ex.printStackTrace();
        }
    }//GEN-LAST:event_btnPrevActionPerformed
    
