    private void showData(){
        try {
            this.txtStudno.setText(String.valueOf(rst.getInt("studno")));
            this.txtName.setText(rst.getString("name"));
            this.txtExamno.setText(String.valueOf(rst.getInt("examno")));
            this.txtChi.setText(String.valueOf(rst.getInt("chi")));
            this.txtEng.setText(String.valueOf(rst.getInt("eng")));
            this.txtMath.setText(String.valueOf(rst.getInt("math")));
            this.lblRecord.setText("第: "+rst.getRow()+" 筆/共: "+RecordCount+" 筆!");
        } catch (SQLException ex) {
            ex.printStackTrace();
        }
    }
